import OpenAI from "openai";
import type { Product } from "@shared/schema";

const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

export interface AIRecommendation {
  products: Product[];
  explanation: string;
  confidence: number;
}

export async function getProductRecommendations(
  query: string, 
  allProducts: Product[]
): Promise<AIRecommendation> {
  try {
    // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: `You are an AI assistant for ArtisanAI, a marketplace for handcrafted items. 
          Your job is to recommend products based on user queries. 

          Available products: ${JSON.stringify(allProducts.map(p => ({
            id: p.id,
            name: p.name,
            description: p.description,
            category: p.category,
            price: p.price,
            artisanName: p.artisanName,
            location: p.location
          })))}

          Analyze the user query and return recommendations in this JSON format:
          {
            "recommendedProductIds": ["product_id1", "product_id2", ...],
            "explanation": "Brief explanation of why these products match the query",
            "confidence": 0.85
          }

          Consider:
          - Price ranges (‚Çπ, $, ‚Ç¨)
          - Categories (pottery, jewelry, textiles, woodwork, etc.)
          - Materials and craftsmanship
          - Location preferences
          - Style and aesthetic preferences

          Be helpful and conversational in your explanation.`
        },
        {
          role: "user",
          content: query
        }
      ],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");

    const recommendedProducts = allProducts.filter(product => 
      result.recommendedProductIds?.includes(product.id)
    );

    return {
      products: recommendedProducts,
      explanation: result.explanation || "Here are some products that might interest you.",
      confidence: Math.max(0, Math.min(1, result.confidence || 0.5))
    };
  } catch (error) {
    console.error("OpenAI API Error:", error);

    // Fallback to simple text matching if OpenAI fails
    const searchTerms = query.toLowerCase();
    const fallbackProducts = allProducts.filter(product =>
      product.name.toLowerCase().includes(searchTerms) ||
      product.description.toLowerCase().includes(searchTerms) ||
      product.category.toLowerCase().includes(searchTerms)
    ).slice(0, 6);

    return {
      products: fallbackProducts,
      explanation: "I found some products that might match your query. (AI assistant is temporarily unavailable)",
      confidence: 0.3
    };
  }
}

export async function generateChatResponse(
  userMessage: string,
  products: Product[],
  conversationHistory: string[] = []
): Promise<string> {
  try {
    // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: `You are a friendly AI shopping assistant for ArtisanAI, helping customers discover unique handcrafted items. 

          Be conversational, helpful, and enthusiastic about artisan products. 
          Help users find products, learn about artisans, and discover new categories.

          If users ask about specific products, pricing, or categories, refer to the available products.
          Keep responses concise but informative.

          Available products: ${JSON.stringify(products.slice(0, 10).map(p => ({
            name: p.name,
            category: p.category,
            price: p.price,
            artisanName: p.artisanName,
            location: p.location
          })))}`
        },
        ...conversationHistory.slice(-6).map((msg, index) => ({
          role: index % 2 === 0 ? "user" as const : "assistant" as const,
          content: msg
        })),
        {
          role: "user",
          content: userMessage
        }
      ],
    });

    return response.choices[0].message.content || "I'm here to help you find amazing artisan products! What are you looking for?";
  } catch (error) {
    console.error("OpenAI Chat Error:", error);
    return "I'm having trouble connecting right now, but I'm here to help you discover amazing handcrafted items! Try asking about specific categories like jewelry, pottery, or textiles.";
  }
}

client/src/components/ai-chat-widget.tsx.content:
import { useState, useRef, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { MessageCircle, X, Send, Bot, User } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";

interface ChatMessage {
  id: string;
  content: string;
  sender: "user" | "ai";
  timestamp: Date;
}

export default function AIChatWidget() {
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState<ChatMessage[]>([
    {
      id: "1",
      content: "Hi! I'm your AI shopping assistant. Ask me anything like 'Show me pottery under ‚Çπ1000' or 'Find handmade jewelry'.",
      sender: "ai",
      timestamp: new Date(),
    },
  ]);
  const [sessionId, setSessionId] = useState<string>();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const chatMutation = useMutation({
    mutationFn: (query: string) => api.sendChatMessage(query, sessionId),
    onSuccess: (data) => {
      setSessionId(data.sessionId);

      const aiMessage: ChatMessage = {
        id: Date.now().toString() + "_ai",
        content: data.response,
        sender: "ai",
        timestamp: new Date(data.timestamp),
      };

      setMessages(prev => [...prev, aiMessage]);
    },
    onError: (error) => {
      toast({
        title: "Chat Error",
        description: error.message || "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!message.trim()) return;

    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      content: message.trim(),
      sender: "user",
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setMessage("");

    chatMutation.mutate(userMessage.content);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage(e);
    }
  };

  return (
    <div className="floating-widget" data-testid="ai-chat-widget">
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: 100, scale: 0.8 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 100, scale: 0.8 }}
            className="mb-4"
          >
            <Card className="w-80 h-96 glass-effect shadow-2xl overflow-hidden">
              {/* Chat Header */}
              <div className="gradient-bg p-4 flex items-center justify-between" data-testid="chat-header">
                <div className="flex items-center space-x-2">
                  <div className="w-8 h-8 bg-white rounded-full flex items-center justify-center">
                    <Bot className="h-4 w-4 text-primary" />
                  </div>
                  <div>
                    <h4 className="font-semibold text-white" data-testid="text-chat-title">AI Assistant</h4>
                    <p className="text-xs text-white/80" data-testid="text-chat-status">Online</p>
                  </div>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setIsOpen(false)}
                  className="text-white hover:bg-white/20"
                  data-testid="button-close-chat"
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>

              {/* Chat Messages */}
              <div className="h-64 overflow-y-auto p-4 space-y-3 bg-card/50" data-testid="chat-messages">
                {messages.map((msg) => (
                  <div
                    key={msg.id}
                    className={`flex items-start space-x-2 ${
                      msg.sender === "user" ? "justify-end" : "justify-start"
                    }`}
                    data-testid={`message-${msg.sender}-${msg.id}`}
                  >
                    {msg.sender === "ai" && (
                      <div className="w-6 h-6 bg-primary rounded-full flex items-center justify-center flex-shrink-0">
                        <Bot className="h-3 w-3 text-white" />
                      </div>
                    )}

                    <div
                      className={`max-w-xs p-3 rounded-lg ${
                        msg.sender === "user"
                          ? "bg-primary text-white"
                          : "bg-muted text-foreground"
                      }`}
                    >
                      <p className="text-sm">{msg.content}</p>
                    </div>

                    {msg.sender === "user" && (
                      <div className="w-6 h-6 bg-accent rounded-full flex items-center justify-center flex-shrink-0">
                        <User className="h-3 w-3 text-white" />
                      </div>
                    )}
                  </div>
                ))}

                {chatMutation.isPending && (
                  <div className="flex items-start space-x-2" data-testid="typing-indicator">
                    <div className="w-6 h-6 bg-primary rounded-full flex items-center justify-center flex-shrink-0">
                      <Bot className="h-3 w-3 text-white" />
                    </div>
                    <div className="bg-muted p-3 rounded-lg">
                      <div className="flex space-x-1">
                        <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse" />
                        <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse" style={{ animationDelay: '0.2s' }} />
                        <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse" style={{ animationDelay: '0.4s' }} />
                      </div>
                    </div>
                  </div>
                )}

                <div ref={messagesEndRef} />
              </div>

              {/* Chat Input */}
              <form onSubmit={handleSendMessage} className="p-4 border-t border-border bg-card" data-testid="chat-input-form">
                <div className="flex space-x-2">
                  <Input
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Ask about products..."
                    className="flex-1 bg-input border-border focus:ring-primary"
                    disabled={chatMutation.isPending}
                    data-testid="input-chat-message"
                  />
                  <Button
                    type="submit"
                    size="sm"
                    className="gradient-bg hover:opacity-90"
                    disabled={chatMutation.isPending || !message.trim()}
                    data-testid="button-send-message"
                  >
                    <Send className="h-4 w-4" />
                  </Button>
                </div>
              </form>
            </Card>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Chat Toggle Button */}
      <Button
        onClick={() => setIsOpen(!isOpen)}
        className="w-14 h-14 gradient-bg rounded-full shadow-lg hover:scale-110 transition-transform animate-glow"
        data-testid="button-toggle-chat"
      >
        <MessageCircle className="h-6 w-6 text-white" />
      </Button>
    </div>
  );
}

